        var name2 = child.getDisplayName();
        if(name2 == 'std_coal'){
            var obj = {
                "energyTypeId": "std_coal",
                "thisMonthUsage": 0,
                "lastMonthUsage": 0,
                "lastMonthThisUsage": 0,
                "ratio": 0,
                "thisMonthPlan": 0,
                "lastMonthPlan": 0
            };
            for(var i=0;i<val.length;i++){
                var thisUsage = val[i].thisMonthUsage?val[i].thisMonthUsage:0;//当月使用量
                var thisPlan = val[i].thisMonthPlan?val[i].thisMonthPlan:0;//当月计划量
                var lastThisUsage = val[i].lastMonthThisUsage;//当月同期使用量
                var stdCoalCoeff = val[i].stdCoalCoeff;//折标系数
                var ratio = val[i].ratio?val[i].ratio:0;//同期环比
                var lastUsage = val[i].lastMonthUsage?val[i].lastMonthUsage:0;//上月使用量
                var lastPlan = val[i].lastMonthPlan?val[i].lastMonthPlan:0;//上月计划量

                obj.thisMonthUsage += parseFloat(thisUsage * stdCoalCoeff);
                obj.lastMonthUsage += parseFloat(lastUsage * stdCoalCoeff);
                obj.lastMonthThisUsage += parseFloat(lastThisUsage * stdCoalCoeff);
                obj.ratio += parseFloat(ratio * stdCoalCoeff);
                obj.thisMonthPlan += parseFloat(thisPlan * stdCoalCoeff);
                obj.lastMonthPlan += parseFloat(lastPlan * stdCoalCoeff);
            }
            obj.thisMonthUsage = parseFloat(obj.thisMonthUsage.toFixed(2));
            obj.lastMonthUsage = parseFloat(obj.lastMonthUsage.toFixed(2));
            obj.lastMonthThisUsage = parseFloat(obj.lastMonthThisUsage.toFixed(2));
            obj.ratio = parseFloat(obj.ratio.toFixed(2));
            obj.thisMonthPlan = parseFloat(obj.thisMonthPlan.toFixed(2));
            obj.lastMonthPlan = parseFloat(obj.lastMonthPlan.toFixed(2));
            if(Object.prototype.toString.call(val)== '[object Array]') val.push(obj);
        }
    });
//console.log(val);
    data.eachChild(function(child) {
        var name = child.getDisplayName();
        for(var i=0;i<val.length;i++){
            if(name == val[i].energyTypeId){
                child.eachChild(function(node){
                    var thisMonthUsage = val[i].thisMonthUsage;//当月使用量
                    var thisMonthPlan = val[i].thisMonthPlan;//当月计划量

                    var lastMonthThisUsage = val[i].lastMonthThisUsage;//当月同期使用量
                    var stdCoalCoeff = val[i].stdCoalCoeff;//折标系数
                    var ratio = val[i].ratio;//同期环比

                    var lastMonthUsage = val[i].lastMonthUsage;//上月使用量
                    var lastMonthPlan = val[i].lastMonthPlan;//上月计划量

                    var displayName = node.getDisplayName();
                    if(displayName == 'progress'){
                        var num1 = 0;
                        var num2 = 0;
                        if(thisMonthUsage && thisMonthPlan){
                            num1 = ((thisMonthUsage/thisMonthPlan)*100).toFixed(2);
                            num1 = parseInt(num1)>100?100:num1;
                        }else{
                             num1 = 0;
                        }
                        if(lastMonthUsage && lastMonthPlan){
                            num2 = ((lastMonthUsage/lastMonthPlan)*100).toFixed(2);
                            num2 = parseInt(num2)>100?100:num2;
                        }else{
                            num2 = 0;
                        }
                        node.setAttrObject({'proArray':[parseFloat(num2),parseFloat(num1)]});
                    }
                    if(displayName == 'curPer'){
                        var num3 = 0;
                        if(thisMonthUsage && thisMonthPlan){
                            num3 = ((thisMonthUsage/thisMonthPlan)*100).toFixed(2);
                        }else{
                            num3 = '--';
                        }
                        node.setAttrObject({'value':num3+'%'});
                        node.a('borderWidth',0);
                        node.a('fontSize','13px arial, sans-serif');
                    }
                    if(displayName == 'lastPer'){
                        var num4 = 0;
                        if(lastMonthUsage && lastMonthPlan){
                            num4 = ((lastMonthUsage/lastMonthPlan)*100).toFixed(2);
                        }else{
                            num4 = '--';
                        }
                        node.setAttrObject({'value':num4+'%'});
                        node.a('borderWidth',0);
                        node.a('fontSize','13px arial, sans-serif');
                    }
                    if(displayName == 'thisMonth'){
                        thisMonthUsage = thisMonthUsage != null?thisMonthUsage.toFixed(2):'--';
                        thisMonthPlan = thisMonthPlan != null?thisMonthPlan.toFixed(2):'--';
                        node.a('value3',thisMonthUsage+' / '+thisMonthPlan);
                    }
                    if(displayName == 'lastMonth'){
                        lastMonthUsage = lastMonthUsage != null?lastMonthUsage.toFixed(2):'--';
                        lastMonthPlan = lastMonthPlan != null?lastMonthPlan.toFixed(2):'--';
                        node.a('value3',lastMonthUsage+' / '+lastMonthPlan);
                    }
                    if(displayName == 'ratio'){
                        ratio = ratio!=null?ratio.toFixed(2):'--';
                        node.a('value3',ratio);
                        // if(parseInt(ratio) > 0){
