                        // }else{
                        //     node.a('color','#7cc27c');
                        // }
                    }
                    if(displayName == 'arrow'){
                        var attrObject = node.getAttrObject();
                        var update2 = attrObject.update;
                        if(val[i].ratio) update2(node,parseFloat(ratio));
                    }
                })
            }
        }
    
    });
    
}

