__ht__function(data,res){
    var val5 = res;
    var i = 0;
    data.eachChild(function(child) {
        var name = child.getDisplayName();
            if(name == 'jet-text' && val5[i]){
                console.log(val5[i]);
                var value = val5[i].lastValue;
                if(value == null){
                    child.a('value', '--');
                }
                else if(value == 0){
                    child.a('value', 0);
                }
                else{
                    child.a('value',value?parseFloat(value).toFixed(2):'--');
                }
                i++;
            }
    });
}

__ht__function update(data, valueObj) {
    //此函数需要应用程序在收到后台查询结果后主动调用，并传入对应的参数
    //data:当前关联的ht Data对象
    //valueObj:后台根据当前对象自定义属性query的设置值的查询结果，类型为JSON
    console.log(data);
    console.log(valueObj);

    /*
        enum为对象数组，示例如下：
        [
            {
                "value": 0,
                "desc": "开"
            },
            {
                "value": 1,
                "desc": "关"
            }
        ]
    */
    var enumobj = data.a('enum');
    console.log(enumobj);
    if (enumobj != null && enumobj.length > 0) {
        var flag = false;
        for (var i = 0; i < enumobj.length; i++) {
            if (valueObj == enumobj[i].value) {
                data.a('value', enumobj[i].desc);
                flag = true;
                break;
            }
        }
        if (!flag) {
            data.a('value', valueObj);
        }
    }
    else {
        if(valueObj != null){
            data.a('value', valueObj.toFixed(1));
        }
        else{
            data.a('value', '?');
        }
    }
    
}

