renderHTML:
__ht__function(data, gv, cache) {

    var xxx = data.a('dataY');
    var series = [];
    if (xxx.length > 0) {
        for (var i = 0; i < xxx.length; i++) {
            var serie = {};
            serie.name = xxx[i].name;
            serie.type = 'line';
            serie.data = xxx[i].data;
            serie.areaStyle = data.a('area') ? {} : null;
            serie.smooth = data.a('lineSmooth');
            series[i] = serie;
        }
    }

    var option = {
        backgroundColor: data.a('background'),
        textStyle: {
            color: data.a('textColor')
        },
        legend: {
            top: 'bottom',
            textStyle: {
                color: data.a('textColor')
            }
        },
        tooltip: {
            trigger: 'axis',
            axisPointer: {
                type: 'line'
            }
        },
        grid: {
            // left: '3%',
            right: '3%',
            bottom: '10%',
            top: '10%'
            // containLabel: true
        },
        xAxis: [
            {
                type: 'category',
                data: data.a('dataX')
            }
        ],
        yAxis: [
            {
                type: 'value',
                scale: true,
                name : data.a('yAxisName'),
                splitLine:{show: false}//去除网格线
            }
        ],
        series: series
    };

    if (!cache.htmlView) {
        cache.htmlView = document.createElement('div');
        cache.htmlView.style.position = 'absolute';
        cache.htmlChart = echarts.init(cache.htmlView, data.a('theme'));

        // layoutHTML will be called when the data needs to be drawing
        cache.htmlView.layoutHTML = function () {
            gv.layoutHTML(data, cache.htmlView, false);
            cache.htmlChart.resize();
        };
    }

    cache.htmlChart.setOption(option);

    // html for showing
    return cache.htmlView;
}
